mapper.update:
UPDATE USERS
   SET MOBILE = #{mobile}
 WHERE USER_ID = #{userId}
   AND STATUS = '1'

UPDATE USERS
   SET SEX = #{sex}
 WHERE USER_ID = #{userId}
   AND STATUS = '1'

UPDATE USERS
   SET STATUS = '0'	
     , REMARK = #{remark}
 WHERE USER_ID = #{userId}
   AND STATUS = '1'

mapper.select:
SELECT COUNT(*) AS cnt
  FROM USERS
 WHERE USER_ID = #{userId}
